import styled from "styled-components";


export const Header = styled.div`
height: 48px;
max-height: 44px;
width: 100%;
background: rgba(0,0,0,0.8);
position: absolute;
z-index: 9999;
top: 0;
right: 0;
left: 0; 
display: flex;
flex-direction: row;
align-items: center;
justify-content: center;
gap: 25px; //coloca uma distancia só entre os itens 

`;

export const ItemHeader = styled.div`
display: flex;

`
