import styled from "styled-components";
interface ImageOptionsProductAplleType {
    widhtImage?: string;
}

interface SpanNewOptionProductAppleType{
    type?: string;
}

interface PurchaseButtonType {
    background: string;
    color: string;
    underline?: boolean;
}

export const Header = styled.div`
    height: 48px;
    max-height: 44px;
    width: 100%;
    background: rgba(0,0,0,0.8);
    z-index: 9999;
    top: 0;
    right: 0;
    left: 0; 
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: 35px; //coloca uma distancia só entre os itens
`;

export const ItemHeader = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center; 
`;

export const TextHeader = styled.div`
    font-size: 13px;
    color: #f5f5f7;
    opacity: .8;
    font-family: 'MyriadProLight';
    cursor: pointer; 
    //letter-spacing: -0.1em; espaçamento das letras
`;

export const OptionsProductsApple = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    height: 100px;
    background-color: white;
    gap: 30px;
`;

export const ItemOptionsProductApple = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 5px;
    cursor: pointer;
`;

export const ImageOptionsProductAplle = styled.img<ImageOptionsProductAplleType>`
    width: ${(props) => props.widhtImage ? props.widhtImage : '35px'};
`;

export const TextOptionsProductAplle = styled.a`
    font-family: 'MyriadProRegular';
    color: rgba(0,0,0,0.8);
    font-size: 12px;
    justify-content: center;
`;

export const SpanNewOptionProductApple = styled.span<SpanNewOptionProductAppleType>`
    font-family: 'MyriadProRegular';
    color: #f56300;
    position: ${(props) => props.type === 'span-new-option' ? 'absolute' : 'relative'}; // vamos receber as props deste componente, e a gente vai fazer uma verificação: se props.type  for igual a span-new-option eu vou deicar como absolute, caso contrário será relative.
    margin-top: ${(props) => props.type === 'span-new-option' ? '80px' : '0px'};
    font-size: ${(props) => props.type === 'span-new-option' ? '10px' : '16px'};
    font-weight: ${(props) => props.type === 'span-new-option' ? '400' : 'bold'};
`;

export const AppleBuyBlock = styled.div`
    height: 38px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    background: #f5f6f7;
    margin-top: 20px;
    color: rgba(0,0,0,0.8);
    letter-spacing: -.016em; //espaçamento das letras

p {
    font-family: 'MyriadProRegular';
    font-size: 14px;
    font-weight: 400;

a {
    color: #06c;
    text-decoration: none;
}

a:hover{
    text-decoration: underline;
}
}
`;

export const BannerArea = styled.div`
  height: 700px;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 60px 0;

  h1 {
    font-size: 56px;
    color: #1d1d1f;
    font-family: 'MyriadProRegular';
    font-weight: bold;
    margin-top: 2px
  }

  h2 {
    font-size: 28px;
    color: #1d1d1f;
    font-family: 'MyriadProRegular';
    font-weight: bold;
    margin-top: -40px
  }

  p{

    font-size: 17px;
    color: #1d1d1f;
    font-family: 'MyriadProLight';
    margin-top: -2px;
    font-weight: 600;

  }
`;

export const BannerAreaButtons = styled.div`
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
gap: 5px;
`;

export const PurchaseButton = styled.button<PurchaseButtonType>`
    background: ${(props) => props.background};
    color: ${(props) => props.color};
    cursor: pointer;
    height: 32px;
    width: 84px;
    display: inline-block;
    text-align: center;
    white-space: nowrap;
    font-size: 15px;
    line-height: 1.17648;
    font-weight: 300;
    font-family: 'MyriadProLight';
    border-radius: 980px;
    padding-left: 16px;
    padding-right: 16px;
    padding-top: 8px; 
    padding-bottom: 8px; 
    border: none;

    &:hover {
        text-decoration: ${(props) => props.underline ? 'underline' : 'none'} 
}
    `
