import styled from "styled-components";
interface ImageOptionsProductAplleType {
    widhtImage?: string;
}

export const Header = styled.div`
height: 48px;
max-height: 44px;
width: 100%;
background: rgba(0,0,0,0.8);
z-index: 9999;
top: 0;
right: 0;
left: 0; 
display: flex;
flex-direction: row;
align-items: center;
justify-content: center;
gap: 35px; //coloca uma distancia só entre os itens
`;

export const ItemHeader = styled.div`
display: flex;
flex-direction: row;
justify-content: center; 
`;

export const TextHeader = styled.div`
font-size: 13px;
color: #f5f5f7;
opacity: .8;
font-family: 'MyriadProLight';
cursor: pointer; 
//letter-spacing: -0.1em; espaçamento das letras
`;

export const OptionsProductsApple = styled.div`
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
height: 100px;
background-color: white;
gap: 30px;
`;

export const ItemOptionsProductApple = styled.div`
display: flex;
flex-direction: column;
align-items: center;
gap: 5px;
cursor: pointer;
`;

export const ImageOptionsProductAplle = styled.img<ImageOptionsProductAplleType>`
width: ${(props) => props.widhtImage ? props.widhtImage : '35px'};
`;

export const TextOptionsProductAplle = styled.a`
font-family: 'MyriadProRegular';
color: rgba(0,0,0,0.8);
font-size: 12px;
justify-content: center;
`;

export const SpanNewOptionProductApple = styled.span`
font-family: 'MyriadProRegular';
color: #f56300;
font-size: 10px;
position: absolute;
height: 400px;
margin-top: 80px;
`;

export const AppleBuyBlock = styled.div`
height: 38px;
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
background: #f5f6f7;
margin-top: 15px;

p {
font-family: 'MyriadProRegular';
font-size: 13px;
font-weight: 400;

a {
    color: #06c;
    text-decoration: none;
}

a:hover{
    text-decoration: underline;
}
}
`;
