import styled from "styled-components";

export const Header = styled.div`
height: 48px;
max-height: 44px;
width: 100%;
background: rgba(0,0,0,0.8);
position: absolute;
z-index: 9999;
top: 0;
right: 0;
left: 0; 
display: flex;
flex-direction: row;
align-items: center;
justify-content: center;
gap: 35px; //coloca uma distancia só entre os itens
`;

export const ItemHeader = styled.div`
display: flex;
flex-direction: row;
justify-content: center; 
`

export const TextHeader = styled.div`
font-size: 13px;
color: #f5f5f7;
opacity: .8;
font-family: 'MyriadProLight';
cursor: pointer;
letter-spacing: -0.1 //espaçamento das letras
`

export const OptionsProductsApple = styled.div`
position: relative
height: 100%;
z-index: 1; 
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
`
